contracts/KycAmlContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title KycAmlContract
 * @dev Basic KYC/AML compliance contract for CeloSwift
 * @author CeloSwift Team
 */
contract KycAmlContract is Ownable, Pausable {

    constructor() Ownable(msg.sender) {}

    struct KycRecord {
        address user;
        string documentHash;
        string documentType; // "passport", "id_card", "drivers_license"
        uint256 verificationDate;
        uint256 expiryDate;
        KycStatus status;
        string country;
        string riskLevel; // "low", "medium", "high"
        bool isActive;
    }

    struct AmlCheck {
        address user;
        uint256 checkDate;
        bool isSanctioned;
        bool isPep; // Politically Exposed Person
        string riskScore;
        string notes;
    }

    enum KycStatus {
        Pending,
        Verified,
        Rejected,
        Expired,
        Suspended
    }

    // State variables
    mapping(address => KycRecord) public kycRecords;
    mapping(address => AmlCheck[]) public amlChecks;
    mapping(string => bool) public sanctionedAddresses;
    mapping(string => bool) public pepAddresses;

    address[] public verifiedUsers;
    uint256 public kycCounter;

    // KYC requirements
    uint256 public constant KYC_EXPIRY_DAYS = 365; // 1 year
    uint256 public constant MIN_AMOUNT_REQUIRING_KYC = 1000 * 10**18; // 1000 tokens

    // Events
    event KycSubmitted(
        address indexed user,
        string documentHash,
        string documentType,
        string country
    );

    event KycVerified(
        address indexed user,
        KycStatus status,
        uint256 expiryDate
    );

    event KycRejected(
        address indexed user,
        string reason
    );

    event AmlCheckPerformed(
        address indexed user,
        bool isSanctioned,
        bool isPep,
        string riskScore
    );

    event SanctionedAddressAdded(
        string indexed addressHash,
        bool sanctioned
    );

    event PepAddressAdded(
        string indexed addressHash,
        bool pep
    );

    // Modifiers
    modifier onlyKycVerified(address user) {
        require(
            kycRecords[user].status == KycStatus.Verified && 
            kycRecords[user].isActive &&
            kycRecords[user].expiryDate > block.timestamp,
            "KYC verification required"
        );
        _;
    }

    /**
     * @dev Submit KYC documents for verification
     */
    function submitKyc(
        string memory documentHash,
        string memory documentType,
        string memory country
    ) external whenNotPaused {
        require(bytes(documentHash).length > 0, "Document hash required");
        require(bytes(documentType).length > 0, "Document type required");
        require(bytes(country).length > 0, "Country required");

        // Check if user already has pending or verified KYC
        require(
            kycRecords[msg.sender].status == KycStatus.Rejected ||
            kycRecords[msg.sender].status == KycStatus.Expired ||
            kycRecords[msg.sender].status == KycStatus.Suspended,
            "KYC already submitted or verified"
        );

        kycCounter++;

        kycRecords[msg.sender] = KycRecord({
            user: msg.sender,
            documentHash: documentHash,
            documentType: documentType,
            verificationDate: 0,
            expiryDate: 0,
            status: KycStatus.Pending,
            country: country,
            riskLevel: "medium", // Default risk level
            isActive: false
        });

        emit KycSubmitted(msg.sender, documentHash, documentType, country);
    }

    /**
     * @dev Verify KYC (only owner or authorized verifier)
     */
    function verifyKyc(
        address user,
        KycStatus status,
        string memory riskLevel
    ) external onlyOwner {
        require(kycRecords[user].status == KycStatus.Pending, "Invalid status");
        require(bytes(riskLevel).length > 0, "Risk level required");

        kycRecords[user].status = status;
        kycRecords[user].riskLevel = riskLevel;
        kycRecords[user].verificationDate = block.timestamp;

        if (status == KycStatus.Verified) {
            kycRecords[user].expiryDate = block.timestamp + (KYC_EXPIRY_DAYS * 1 days);
            kycRecords[user].isActive = true;
            verifiedUsers.push(user);
        } else {
            kycRecords[user].isActive = false;
        }

        emit KycVerified(user, status, kycRecords[user].expiryDate);
    }

    /**
     * @dev Reject KYC with reason
     */
    function rejectKyc(address user, string memory reason) external onlyOwner {
        require(kycRecords[user].status == KycStatus.Pending, "Invalid status");

        kycRecords[user].status = KycStatus.Rejected;
        kycRecords[user].isActive = false;

        emit KycRejected(user, reason);
    }

    /**
     * @dev Perform AML check
     */
    function performAmlCheck(address user) external onlyOwner {
        // In a real implementation, this would integrate with AML databases
        // For testnet, we'll simulate the check

        bool isSanctioned = sanctionedAddresses[addressToHash(user)];
        bool isPep = pepAddresses[addressToHash(user)];

        string memory riskScore = "low";
        if (isSanctioned) {
            riskScore = "high";
        } else if (isPep) {
            riskScore = "medium";
        }

        amlChecks[user].push(AmlCheck({
            user: user,
            checkDate: block.timestamp,
            isSanctioned: isSanctioned,
            isPep: isPep,
            riskScore: riskScore,
            notes: "Automated AML check"
        }));

        emit AmlCheckPerformed(user, isSanctioned, isPep, riskScore);
    }

    /**
     * @dev Add sanctioned address
     */
    function addSanctionedAddress(string memory addressHash, bool sanctioned) external onlyOwner {
        sanctionedAddresses[addressHash] = sanctioned;
        emit SanctionedAddressAdded(addressHash, sanctioned);
    }

    /**
     * @dev Add PEP address
     */
    function addPepAddress(string memory addressHash, bool pep) external onlyOwner {
        pepAddresses[addressHash] = pep;
        emit PepAddressAdded(addressHash, pep);
    }

    /**
     * @dev Suspend user KYC
     */
    function suspendKyc(address user) external onlyOwner {
        require(kycRecords[user].status == KycStatus.Verified, "User not verified");
        kycRecords[user].status = KycStatus.Suspended;
        kycRecords[user].isActive = false;
    }

    /**
     * @dev Reactivate suspended KYC
     */
    function reactivateKyc(address user) external onlyOwner {
        require(kycRecords[user].status == KycStatus.Suspended, "User not suspended");
        kycRecords[user].status = KycStatus.Verified;
        kycRecords[user].isActive = true;
    }

    /**
     * @dev Check if amount requires KYC
     */
    function requiresKyc(uint256 amount) external pure returns (bool) {
        return amount >= MIN_AMOUNT_REQUIRING_KYC;
    }

    /**
     * @dev Get KYC record
     */
    function getKycRecord(address user) external view returns (KycRecord memory) {
        return kycRecords[user];
    }

    /**
     * @dev Get AML checks for user
     */
    function getAmlChecks(address user) external view returns (AmlCheck[] memory) {
        return amlChecks[user];
    }

    /**
     * @dev Get latest AML check
     */
    function getLatestAmlCheck(address user) external view returns (AmlCheck memory) {
        require(amlChecks[user].length > 0, "No AML checks found");
        return amlChecks[user][amlChecks[user].length - 1];
    }

    /**
     * @dev Get all verified users
     */
    function getVerifiedUsers() external view returns (address[] memory) {
        return verifiedUsers;
    }

    /**
     * @dev Check if user is KYC verified
     */
    function isKycVerified(address user) external view returns (bool) {
        return kycRecords[user].status == KycStatus.Verified && 
               kycRecords[user].isActive &&
               kycRecords[user].expiryDate > block.timestamp;
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Internal function to convert address to hash string
     */
    function addressToHash(address addr) internal pure returns (string memory) {
        return string(abi.encodePacked("0x", toHexString(uint160(addr), 20)));
    }

    /**
     * @dev Internal function to convert uint to hex string
     */
    function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
        bytes memory buffer = new bytes(2 * length);
        bytes memory hexChars = "0123456789abcdef";
        for (uint256 i = 2 * length; i > 0; --i) {
            buffer[i - 1] = hexChars[value & 0xf];
            value >>= 4;
        }
        return string(buffer);
    }
}

contracts/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockERC20
 * @dev Mock ERC20 token for testing purposes
 * @author CeloSwift Team
 */
contract MockERC20 is ERC20, Ownable {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) Ownable(msg.sender) {
        _decimals = 18;
        _mint(msg.sender, initialSupply);
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) public onlyOwner {
        _burn(from, amount);
    }
}

contracts/PhoneRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title PhoneRegistry
 * @dev Contract for managing phone number to address mappings
 * @author CeloSwift Team
 */
contract PhoneRegistry is Ownable, Pausable {

    constructor() Ownable(msg.sender) {}

    struct PhoneRecord {
        string phoneNumber;
        address walletAddress;
        string name;
        bool isActive;
        uint256 registrationDate;
        uint256 lastUpdate;
        bool isVerified;
    }

    struct VerificationRequest {
        string phoneNumber;
        address requester;
        uint256 timestamp;
        bool isVerified;
        string verificationCode;
    }

    // State variables
    mapping(string => PhoneRecord) public phoneRecords;
    mapping(address => string) public addressToPhone;
    mapping(string => VerificationRequest) public verificationRequests;

    string[] public registeredPhones;
    uint256 public verificationCounter;

    // Verification settings
    uint256 public constant VERIFICATION_EXPIRY = 10 minutes;
    uint256 public constant MAX_VERIFICATION_ATTEMPTS = 3;

    mapping(string => uint256) public verificationAttempts;

    // Events
    event PhoneRegistered(
        string indexed phoneNumber,
        address indexed walletAddress,
        string name
    );

    event PhoneUpdated(
        string indexed phoneNumber,
        address indexed newAddress,
        string name
    );

    event PhoneDeactivated(
        string indexed phoneNumber,
        address indexed walletAddress
    );

    event VerificationRequested(
        string indexed phoneNumber,
        address indexed requester,
        uint256 timestamp
    );

    event PhoneVerified(
        string indexed phoneNumber,
        address indexed walletAddress
    );

    event VerificationFailed(
        string indexed phoneNumber,
        string reason
    );

    // Modifiers
    modifier onlyPhoneOwner(string memory phoneNumber) {
        require(
            phoneRecords[phoneNumber].walletAddress == msg.sender,
            "Not phone owner"
        );
        _;
    }

    modifier onlyActivePhone(string memory phoneNumber) {
        require(
            phoneRecords[phoneNumber].isActive,
            "Phone not active"
        );
        _;
    }

    /**
     * @dev Register a phone number with wallet address
     */
    function registerPhone(
        string memory phoneNumber,
        string memory name
    ) external whenNotPaused {
        require(bytes(phoneNumber).length > 0, "Phone number required");
        require(bytes(name).length > 0, "Name required");
        require(
            phoneRecords[phoneNumber].walletAddress == address(0),
            "Phone already registered"
        );
        require(
            bytes(addressToPhone[msg.sender]).length == 0,
            "Address already has phone"
        );

        phoneRecords[phoneNumber] = PhoneRecord({
            phoneNumber: phoneNumber,
            walletAddress: msg.sender,
            name: name,
            isActive: true,
            registrationDate: block.timestamp,
            lastUpdate: block.timestamp,
            isVerified: false
        });

        addressToPhone[msg.sender] = phoneNumber;
        registeredPhones.push(phoneNumber);

        emit PhoneRegistered(phoneNumber, msg.sender, name);
    }

    /**
     * @dev Update phone record (only owner)
     */
    function updatePhone(
        string memory phoneNumber,
        string memory newName
    ) external 
        onlyPhoneOwner(phoneNumber)
        onlyActivePhone(phoneNumber)
    {
        require(bytes(newName).length > 0, "Name required");

        phoneRecords[phoneNumber].name = newName;
        phoneRecords[phoneNumber].lastUpdate = block.timestamp;

        emit PhoneUpdated(phoneNumber, msg.sender, newName);
    }

    /**
     * @dev Deactivate phone record
     */
    function deactivatePhone(string memory phoneNumber) external 
        onlyPhoneOwner(phoneNumber)
        onlyActivePhone(phoneNumber)
    {
        phoneRecords[phoneNumber].isActive = false;
        phoneRecords[phoneNumber].lastUpdate = block.timestamp;

        emit PhoneDeactivated(phoneNumber, msg.sender);
    }

    /**
     * @dev Reactivate phone record
     */
    function reactivatePhone(string memory phoneNumber) external 
        onlyPhoneOwner(phoneNumber)
    {
        require(!phoneRecords[phoneNumber].isActive, "Phone already active");

        phoneRecords[phoneNumber].isActive = true;
        phoneRecords[phoneNumber].lastUpdate = block.timestamp;

        emit PhoneUpdated(phoneNumber, msg.sender, phoneRecords[phoneNumber].name);
    }

    /**
     * @dev Request phone verification
     */
    function requestVerification(string memory phoneNumber) external 
        onlyPhoneOwner(phoneNumber)
        onlyActivePhone(phoneNumber)
    {
        require(
            verificationAttempts[phoneNumber] < MAX_VERIFICATION_ATTEMPTS,
            "Max verification attempts reached"
        );

        verificationCounter++;
        string memory verificationCode = generateVerificationCode();

        verificationRequests[phoneNumber] = VerificationRequest({
            phoneNumber: phoneNumber,
            requester: msg.sender,
            timestamp: block.timestamp,
            isVerified: false,
            verificationCode: verificationCode
        });

        verificationAttempts[phoneNumber]++;

        emit VerificationRequested(phoneNumber, msg.sender, block.timestamp);
    }

    /**
     * @dev Verify phone with code
     */
    function verifyPhone(
        string memory phoneNumber,
        string memory verificationCode
    ) external onlyPhoneOwner(phoneNumber) {
        VerificationRequest storage request = verificationRequests[phoneNumber];

        require(request.requester == msg.sender, "Invalid requester");
        require(!request.isVerified, "Already verified");
        require(
            block.timestamp <= request.timestamp + VERIFICATION_EXPIRY,
            "Verification expired"
        );
        require(
            keccak256(bytes(request.verificationCode)) == keccak256(bytes(verificationCode)),
            "Invalid verification code"
        );

        request.isVerified = true;
        phoneRecords[phoneNumber].isVerified = true;
        phoneRecords[phoneNumber].lastUpdate = block.timestamp;

        emit PhoneVerified(phoneNumber, msg.sender);
    }

    /**
     * @dev Get address by phone number
     */
    function getAddressByPhone(string memory phoneNumber) external view returns (address) {
        require(phoneRecords[phoneNumber].isActive, "Phone not active");
        return phoneRecords[phoneNumber].walletAddress;
    }

    /**
     * @dev Get phone by address
     */
    function getPhoneByAddress(address walletAddress) external view returns (string memory) {
        return addressToPhone[walletAddress];
    }

    /**
     * @dev Get phone record
     */
    function getPhoneRecord(string memory phoneNumber) external view returns (PhoneRecord memory) {
        return phoneRecords[phoneNumber];
    }

    /**
     * @dev Check if phone is registered and active
     */
    function isPhoneRegistered(string memory phoneNumber) external view returns (bool) {
        return phoneRecords[phoneNumber].isActive;
    }

    /**
     * @dev Check if phone is verified
     */
    function isPhoneVerified(string memory phoneNumber) external view returns (bool) {
        return phoneRecords[phoneNumber].isActive && phoneRecords[phoneNumber].isVerified;
    }

    /**
     * @dev Get all registered phones
     */
    function getAllRegisteredPhones() external view returns (string[] memory) {
        return registeredPhones;
    }

    /**
     * @dev Get verification request
     */
    function getVerificationRequest(string memory phoneNumber) external view returns (VerificationRequest memory) {
        return verificationRequests[phoneNumber];
    }

    /**
     * @dev Search phones by name (partial match)
     */
    function searchPhonesByName(string memory searchTerm) external view returns (string[] memory) {
        string[] memory results = new string[](registeredPhones.length);
        uint256 count = 0;

        for (uint256 i = 0; i < registeredPhones.length; i++) {
            string memory phone = registeredPhones[i];
            if (phoneRecords[phone].isActive) {
                string memory name = phoneRecords[phone].name;
                if (containsString(name, searchTerm)) {
                    results[count] = phone;
                    count++;
                }
            }
        }

        // Resize array to actual count
        string[] memory finalResults = new string[](count);
        for (uint256 i = 0; i < count; i++) {
            finalResults[i] = results[i];
        }

        return finalResults;
    }

    /**
     * @dev Emergency deactivate phone (only owner)
     */
    function emergencyDeactivatePhone(string memory phoneNumber) external onlyOwner {
        phoneRecords[phoneNumber].isActive = false;
        phoneRecords[phoneNumber].lastUpdate = block.timestamp;

        emit PhoneDeactivated(phoneNumber, phoneRecords[phoneNumber].walletAddress);
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Generate verification code (simplified for testnet)
     */
    function generateVerificationCode() internal view returns (string memory) {
        // In production, this would be a secure random code
        // For testnet, we'll use a simple deterministic code
        uint256 code = uint256(keccak256(abi.encodePacked(
            block.timestamp,
            msg.sender,
            block.difficulty
        ))) % 1000000;

        return string(abi.encodePacked(uint2str(code)));
    }

    /**
     * @dev Convert uint to string
     */
    function uint2str(uint256 _i) internal pure returns (string memory) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k - 1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    /**
     * @dev Check if string contains substring
     */
    function containsString(string memory str, string memory substr) internal pure returns (bool) {
        bytes memory strBytes = bytes(str);
        bytes memory substrBytes = bytes(substr);

        if (substrBytes.length > strBytes.length) {
            return false;
        }

        for (uint256 i = 0; i <= strBytes.length - substrBytes.length; i++) {
            bool found = true;
            for (uint256 j = 0; j < substrBytes.length; j++) {
                if (strBytes[i + j] != substrBytes[j]) {
                    found = false;
                    break;
                }
            }
            if (found) {
                return true;
            }
        }

        return false;
    }
}

